mapper.insert:
INSERT INTO parents
SET uid = #{uid}
    , upw = #{upw}
    , nm = #{nm}
    , phone = #{phone}
    , subphone = #{subPhone}
    , email = #{email}
    , connet = #{connet}
    , auth = #{auth}
    , addr = #{addr}
    , acept = #{acept}

mapper.select:
SELECT uid
FROM parents
WHERE nm = #{nm}
AND phone = #{phone}

mapper.update:
UPDATE parents A
INNER JOIN student B
ON A.parents_id = B.parents_id
SET B.name = #{stuNm}
    , A.nm = #{nm}
    , A.connet = #{connet}
    , A.phone = #{phone}
    , A.subphone = #{subPhone}
    , A.email = #{email}
    , B.stu_eng_nm = #{stuEngNm}
    , A.addr = #{addr}
    , B.etc = #{stuEtc}

UPDATE parents
SET upw = #{newUpw}
WHERE parents_id = #{parentsId}
